lanning = $position . 'Traitements/Planning.php';				echo '<a href=' . $planning . '>Consulter le planning</a>';				?> 			</li>            <li>				<?php				$ressources = $position . 'Traitements/ConsulterRessources.php';				echo '<a href=' . $ressources . '>Consulter les ressources</a>';				?> 			</li>            				<?php				if ($connecte == true) {					echo 'Reserver une ressource';					//'<li> <a href= ' . . '>Reserver une ressource</a> </li>';				}				?>			   	 </ul>	</div></html>